import type {CodeKeywordDefinition} from "ajv/lib/types"
import type {KeywordCxt} from "ajv/lib/compile/validate"
import {validateProperties, error} from "ajv/lib/vocabularies/jtd/properties"

const def: CodeKeywordDefinition = {
  keyword: "optionalProperties",
  schemaType: "object",
  error,
  code(cxt: KeywordCxt) {
    if (cxt.parentSchema.properties) return
    validateProperties(cxt)
  },
}

export default def
