import type {CodeKeywordDefinition} from "ajv/lib/types"
import type {KeywordCxt} from "ajv/lib/compile/validate"
import {checkStrictMode} from "ajv/lib/compile/util"

const def: CodeKeywordDefinition = {
  keyword: ["maxContains", "minContains"],
  type: "array",
  schemaType: "number",
  code({keyword, parentSchema, it}: KeywordCxt) {
    if (parentSchema.contains === undefined) {
      checkStrictMode(it, `"${keyword}" without "contains" is ignored`)
    }
  },
}

export default def
